import { StructuredQuery_Filter, StructuredQuery_Value, StructuredQuery_Limit } from '../proto/db3_database';
import type { CollectionReference } from '../store/collection';
/**
 * The Query interface defines all external properties of a query.
 *
 * QueryImpl implements this interface to provide memoization for `queryOrderBy`
 * and `queryToTarget`.
 */
export interface Query<T> {
    readonly filters: StructuredQuery_Filter[];
    readonly limit: StructuredQuery_Limit | null;
    readonly collection: CollectionReference<T>;
}
export declare function parseQueryValue(value: unknown): StructuredQuery_Value;
export declare function queryWithAddedFilter<T>(query: Query<T>, filter: StructuredQuery_Filter): Query<T>;
export declare function queryWithLimit<T>(query: Query<T>, limit: number): Query<T>;
