export type WalletType = 'DB3_SECP256K1' | 'DB3_ED25519' | 'MetaMask';
export interface Wallet<P, R> {
    /**
     * Sign the message
     */
    sign(message: P): R;
    /**
     * return the db3 address
     */
    getAddress(): string;
    /**
     * return the type of wallet
     */
    getType(): WalletType;
}
